using UnityEngine;using System.Collections;public class HealthPickup : IPickup{    /// <summary>    /// The amount of health this pickup increases    /// </summary>    public float HealthIncreaseAmount = 1.0f;	// Use this for initialization	protected override void Start ()     {        base.Start();	}		// Update is called once per frame    protected override void Update()    {        base.Update();	}    // Called when something enters the pickups trigger box    protected override void OnTriggerEnter2D(Collider2D other)    {        if (other.tag != "Player")        {            return;        }        ICharacter character = other.GetComponent<ICharacter>();        character.Health += this.HealthIncreaseAmount;        base.OnTriggerEnter2D(other);    }}